import { Router } from "express";
import { methods as datosController } from "./../controllers/datos.controller";

const router = Router();

router.get("/", datosController.getDato);
router.get("/:id", datosController.getDatos);
router.post("/", datosController.addDatos);
router.put("/:id", datosController.updateDatos);
router.delete("/:id", datosController.deleteDatos);

export default router;

