sourcesContent:
import { getConnection } from "../database/database";

const getDato = async (req, res) => {
    try {
        const connection = await getConnection();
        const result = await connection.query("SELECT id, precio, url_imagen from imagenes");
        res.json(result);
    } catch (error) {
        res.status(500);
        res.send(error.message);
    }
};

const getDatos = async (req, res) => {
    try {
        const { cc_nombre } = req.params;
        const connection = await getConnection();
        const result = await connection.query("SELECT cc_nombre, apellido, nombre FROM datos WHERE cc_nombre = ?", cc_nombre);
        res.json(result);
    } catch (error) {
        res.status(500);
        res.send(error.message);
    }
};

const addDatos = async (req, res) => {
    try {
        const { nombre, apellido } = req.body;

        if (nombre === undefined || apellido === undefined) {
            res.status(400).json({ message: "Bad Request. Please fill all field." });
        }

        const datos = { nombre, apellido };
        const connection = await getConnection();
        await connection.query("INSERT INTO datos SET ?", datos);
        res.json({ message: "datos added" });
    } catch (error) {
        res.status(500);
        res.send(error.message);
    }
};

const updateDatos = async (req, res) => {
    try {
        const { id } = req.params;
        const { nombre, apellido } = req.body;

        if (id === undefined || nombre === undefined || apellido === undefined) {
            res.status(400).json({ message: "Bad Request. Please fill all field." });
        }

        const datos = { nombre, apellido };
        const connection = await getConnection();
        const result = await connection.query("UPDATE datos SET ? WHERE id = ?", [nombre, id]);
        res.json(result);
    } catch (error) {
        res.status(500);
        res.send(error.message);
    }
};

const deleteDatos = async (req, res) => {
    try {
        const { id } = req.params;
        const connection = await getConnection();
        const result = await connection.query("DELETE FROM datos WHERE id = ?", id);
        res.json(result);
    } catch (error) {
        res.status(500);
        res.send(error.message);
    }
};

export const methods = {
    getDato,
    getDatos,
    addDatos,
    updateDatos,
    deleteDatos
};

