import { getConnection } from "../database/database";



const getAll = async (req, res) => {
    try {
        const connection = await getConnection();
        const data= await connection.query("SELECT * FROM usuarios");
        console.log(data);
        res.json(data);
    } catch (error) {
        res.status(500);
        res.send(error.message);
    }
}

/*sync function getcc(req, res) {
    try {
        const { cc } = req.params;
        const connection = await getConnection();
        const result = await connection.query("SELECT * FROM usuarios WHERE cc = ?;", cc);
        res.json(result);
    } catch (error) {
        res.status(500);
        res.send(error.message);
    }
}*/

const add = async (req, res) => {

    const { cc, nombre, email, contrasena, departamento, municipio, direccion, telefono} = req.body;
    if (!cc  || !nombre || !email || !contrasena || !departamento || !municipio || !direccion || !telefono) {
        res.status(400).json({ message: "Bad Request. Please fill all field." });
    }
    else{
        try {
            let dato = { cc, nombre, email, contrasena, departamento, municipio, direccion, telefono};
            const connection = await getConnection();
            const record = await connection.query("INSERT INTO usuarios SET  ?", dato );
            dato.id = record.insertId; 
            res.json( dato );
        } catch (error) {
            res.status(400).json({ message: "ya existe." });
            console.log("ya existe")
        }  
    }
};

/*const updatecc = async (req, res) => {
    try {
        const { cc } = req.params;
        const { contrasena } = req.body;

        if (contrasena === undefined) {
            res.status(400).json({ message: "Bad Request. Please fill all field." });
        }

        const datos = { contrasena };
        const connection = await getConnection();
        const result = await connection.query("UPDATE datos SET ? WHERE cc = ?", [contrasena , cc]);
        res.json(result);
    } catch (error) {
        res.status(500);
        res.send(error.message);
    }
};*/





export const methods = {
    //getcc,
    getAll,
    add,
    //updatecc,
    //deleteById
};

